//本地路径
$local = '/www/wwwroot/easypay';
//仓库地址
$remote = 'https://github.com/sheng321/easypay.git';

//密钥，github是密钥，验证方式跟gitee不一样
$secret = '123456';
//获取请求参数
$request = file_get_contents('php://input');


file_put_contents('/www/wwwroot/easypay/secret1.text',$request);

if (empty($request)) {
    die('request is empty');
}
//获取http 头
$headers = getHeaders();

file_put_contents('/www/wwwroot/easypay/secret.text',json_encode($headers));

//github发送过来的签名
$hubSignature = $headers['X-Hub-Signature'];

file_put_contents('/www/wwwroot/secret.text',$hubSignature);
list($algo, $hash) = explode('=', $hubSignature, 2);


// 计算签名
$payloadHash = hash_hmac($algo, $request, $secret);
// 判断签名是否匹配
if ($hash != $payloadHash) {
    die('secret is error');
}

echo shell_exec("cd {$local} && /usr/bin/git pull {$remote} 2>&1");
die('done ' . date('Y-m-d H:i:s', time()));
/**
 * @todo 获取头信息
 */
function getHeaders()
{
    $headers = array();
    //Apache服务器才支持getallheaders函数
    if (!function_exists('getallheaders')) {
        foreach ($_SERVER as $name => $value) {
            if (substr($name, 0, 5) == 'HTTP_') {
                $headers[str_replace(' ', '-', ucwords(strtolower(str_replace('_', ' ', substr($name, 5)))))] = $value;
            }
        }
    } else {
        $headers = getallheaders();
    }
    return $headers;
}